mapper.select:
select from
(select * from user1
type = #{type}
and enabled=#{enabled}
) as u
user1 as u
left join user_authority as ua on u.username = ua.username left join shop as s on u.shop_id = s.id left join distributor as d on s.distributor_id = d.id order by u.username

select from
(select * from distributor
id = #{id}
) as d
distributor as d
left join shop as s on d.id = s.distributor_id left join user1 as u on s.id = u.shop_id

select id, seller_nick as seller_nick, appkey, appsecret, token, refresh_token as refresh_token,
modified, created, last_refresh as last_refresh, auto_sync as auto_sync, type, distributor_id as distributor_id,
auto_sync_store as auto_sync_store, rate from shop

	
		auto_sync = #{auto_sync}
	
	
		and auto_sync_store = #{auto_sync_store}
	
	
		and type=#{type}
	
	
		and seller_nick=#{seller_nick}
