mapper.select:
select from
(select * from user1
type = #{type}
and enabled=#{enabled}
) as u
user1 as u
left join user_authority as ua on u.username = ua.username left join shop as s on u.shop_id = s.id left join distributor as d on s.distributor_id = d.id order by u.username

select from
(select * from distributor
id = #{id}
) as d
distributor as d
left join shop as s on d.id = s.distributor_id left join user1 as u on s.id = u.shop_id

select id, seller_nick as sellerNick, token, refresh_token as refreshToken,
modified, created, last_refresh as lastRefresh, auto_sync as autoSync, type, distributor_id as dId,
auto_sync_store as autoSyncStore, rate from shop

	
		auto_sync = #{autoSync}
	
	
		and auto_sync_store = #{autoSyncStore}
	
	
		and type=#{type}
	
	
		and seller_nick=#{sellerNick}
