(function($) {    $.extend({        getGo: function(url, params, newwindow, a) {                if (!$.isArray(params)) {        	var aparams = [];        	$.each(params, function(key,value){        		aparams.push({name:key,value:value});        	});        	params = aparams.sort(function(a, b){        		if (a.name == 'c')        			return -1;        		if (b.name == 'c')        			return 1;        		if (a.name == 'd')        			return -1;        		if (b.name == 'd')        			return 1;        		return ((a.name < b.name) ? -1 : ((a.name > b.name) ? 1 : 0));        	});        }        var path = url + '?' + $.param(params);		if(-1==url.indexOf("?")){		    path = url + '?' + $.param(params);		}else{            path = url + $.param(params);		}		if (typeof newwindow != 'undefined' && newwindow) {            if (typeof a != 'undefined') {                $(a).attr("href",path);                $(a).attr("target", "_blank");            } else {			    window.open(path, '_blank');            }		} else {            if (typeof a != 'undefined') {                $(a).attr("href",path);            } else {		    	document.location = path;            }		}        },        postGo: function(url, params) {            var $form = $("<form>")                .attr("method", "post")                .attr("action", url);            $.each(params, function(name, value) {                $("<input type='hidden'>")                    .attr("name", name)                    .attr("value", value)                    .appendTo($form);            });            $form.appendTo("body");            $form.submit();        }    });})(jQuery);